import { useState, useEffect } from "react";
import axios from "axios";
import Menu from "../components/Menu";
import MyCart from "../components/MyCart";
import '../styles/OrderStyle.css';
import User from "../images/loginbackground.jpg"; // Assuming you have a user profile image

const Orders = () => {
    const [tables] = useState([1, 2, 3, 4]);
    const [orders, setOrders] = useState({});
    const [cart, setCart] = useState([]);
    const [restaurant, setRestaurant] = useState({ name: "Foodie Place", address: "123 Main St" });
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        tables.forEach((table) => {
            axios.get(`http://localhost:8080/api/orders/table/${table}`)
                .then(response => {
                    setOrders(prevOrders => ({ ...prevOrders, [table]: response.data }));
                })
                .catch(error => console.error(`Error fetching orders for table ${table}:`, error));
        });
    }, [tables]);

    useEffect(() => {
        axios.get("http://localhost:8080/api/menu")
            .then(response => {
                setMenuItems(response.data);
            })
            .catch(error => console.error("Error fetching menu items:", error));
    }, []);

    const addToCart = (item) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(cartItem => cartItem.id === item.id);
            if (existingItem) {
                return prevCart.map(cartItem =>
                    cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
                );
            }
            return [...prevCart, { ...item, quantity: 1 }];
        });
    };

    return (
        <div>
            {/* Navigation Bar */}
            <div className="nav">
                <div id="logo">
                    <h2>FOODIE</h2>
                </div>
                <div id="user">
                    <div className="name">Hello, Kishan</div>
                    <div className="profile">
                        <img src={User} id="img" height="45" width="45" alt="profile" />
                    </div>
                </div>
            </div>

            {/* Content Section */}
            <div id="content">
                <div id="head">
                    <h1 className='hname'>{restaurant.name}</h1>
                    <h5 className='aname'><i className="fa fa-map-marker" style={{ fontSize: 18 }}></i> {restaurant.address}</h5>
                    <div id='items'>
                        <center><h2>Order Now</h2></center>
                        <br />

                        {/* Menu Items */}
                        <div className="grid grid-cols-2 gap-4">
                            {menuItems.map(item => (
                                <Menu key={item.id} item={item} addToCart={addToCart} />
                            ))}
                        </div>
                    </div>
                </div>

                {/* Table Orders Section */}
                <div className="grid grid-cols-2 gap-4 mt-6">
                    {tables.map(table => (
                        <div key={table} className="border p-4 rounded-lg shadow-lg">
                            <h2 className="text-lg font-bold mb-2">Table {table} Orders</h2>
                            {orders[table]?.length ? (
                                <ul>
                                    {orders[table].map((order, index) => (
                                        <li key={`${table}-${order.menuItemId || index}`}>
                                            {order.menuItemName} - {order.totalQuantity}
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No orders yet.</p>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {/* Panel Section */}
            <div id="panel">
                <div id="right">
                    <div id="right-in">
                        <h4>My Cart</h4>

                        <MyCart cart={cart} setCart={setCart} />

                        <div id="total">
                            <p id="total"> Total amount:
                                <span className="spn">{'â‚¹'} {cart.reduce((sum, item) => sum + item.price * item.quantity, 0)}</span>
                            </p>
                            <input id="pay" type="button" value="Pay Now" />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Orders;
