import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import OrderPage from "./components/OrderPage";
import Navbar from "./components/Navbar";

function App() {
  const [user, setUser] = useState(null);

  const handleLogin = async (credentials) => {
    try {
      const response = await axios.post("https://your-backend-api.com/login", credentials);
      setUser(response.data);
    } catch (error) {
      console.error("Login failed", error);
    }
  };

  return (
    <Router>
      {user && <Navbar />}
      <Routes>
        <Route path="/" element={user ? <Navigate to="/dashboard" /> : <Login onLogin={handleLogin} />} />
        <Route path="/dashboard" element={user ? <Dashboard user={user} /> : <Navigate to="/" />} />
        <Route path="/order" element={user ? <OrderPage user={user} /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
