import { useState, useEffect } from "react";
import axios from "axios";
import Menu from "../components/Menu";
import MyCart from "../components/MyCart";
import '../styles/OrderStyle.css';

const Orders = () => {
    const [tables] = useState([1, 2, 3, 4]); // Example table numbers
    const [orders, setOrders] = useState({});
    const [cart, setCart] = useState({}); // Store cart items per table

    useEffect(() => {
        tables.forEach((table) => {
            axios.get(`http://localhost:8080/api/orders/table/${table}`)
                .then(response => {
                    setOrders(prevOrders => ({ ...prevOrders, [table]: response.data }));
                })
                .catch(error => console.error(`Error fetching orders for table ${table}:`, error));
        });
    }, [tables]);

    const addToCart = (table, item) => {
        setCart(prevCart => {
            const tableCart = prevCart[table] || [];
            const existingItem = tableCart.find(cartItem => cartItem.id === item.id);
            if (existingItem) {
                return {
                    ...prevCart,
                    [table]: tableCart.map(cartItem =>
                        cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
                    )
                };
            }
            return {
                ...prevCart,
                [table]: [...tableCart, { ...item, quantity: 1 }]
            };
        });
    };

    return (
        <div className="p-6">
            <div className="nav">
                <div id="logo">
                    <h2>FOODIE</h2>
                </div>
                <div id="user">
                    <div className="name">Hello, Kishan</div>
                    <div className="profile">
                        <img src="/user.png" id="img" height="45" width="45" alt="profile"/>
                    </div>
                </div>
            </div>

            <h1 className="text-2xl font-bold mb-4">Table Orders</h1>
            <div className="grid grid-cols-2 gap-4">
                {tables.map(table => (
                    <div key={table} className="border p-4 rounded-lg shadow-lg">
                        <h2 className="text-lg font-bold mb-2">Table {table} Orders</h2>
                        {orders[table]?.length ? (
                            <ul>
                                {orders[table].map(order => (
                                    <li key={order.menuItemId}>{order.menuItemName} - {order.totalQuantity}</li>
                                ))}
                            </ul>
                        ) : (
                            <p>No orders yet.</p>
                        )}
                    </div>
                ))}
            </div>

            {/* Menu Section */}
            <div className="mt-6">
                <h2 className="text-xl font-bold">Order Now</h2>
                <div className="grid grid-cols-2 gap-4">
                    <Menu setOrder={(table, item) => addToCart(table, item)} />
                </div>
            </div>

            {/* Cart Section per Table */}
            {tables.map(table => (
                <MyCart key={table} table={table} cart={cart[table] || []} setCart={setCart} />
            ))}
        </div>
    );
};

export default Orders;
