import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Menu from "./components/Menu";
import Order from "./components/Order";
import Login from "./components/Login";
import { useState } from "react";

function App() {
  const [user, setUser] = useState(null);

  const handleLogin = async (credentials) => {
    try {
      const response = await axios.post("https://your-backend-api.com/login", credentials);
      setUser(response.data.user); // Adjust based on API response structure
      localStorage.setItem("token", response.data.token); // Save token if needed
    } catch (error) {
      console.error("Login failed", error);
      alert("Login failed. Please check your credentials.");
    }
  };
  
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Login onLogin={setUser} />} />
        <Route path="/menu" element={user ? <Menu /> : <p>Please log in.</p>} />
        <Route path="/orders" element={user ? <Order /> : <p>Please log in.</p>} />
      </Routes>
    </Router>
  );
}

export default App;
