const Menu = ({ item, addToCart }) => {
    return (
        <div className="border p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-bold">{item.name}</h3>
            <p className="text-gray-600">{'\u20B9'} {item.price}</p>
            <button className="btn bg-red-500 text-white px-4 py-2 rounded-md mt-2"
                onClick={() => addToCart(item)}>
                Add to Cart
            </button>
        </div>
    );
};

export default Menu;
